import { describe, expect, it } from 'vitest';

import { formatToPrint } from '../helpers';

describe('helpers', () => {
  it('formats strings', () => {
    expect(formatToPrint(['string 1', 'string 2'])).toMatchInlineSnapshot(`"string 1 string 2"`);
  });

  it('formats errors', () => {
    expect(
      formatToPrint([
        new Error('Message', {
          cause: 'A cause',
        }),
      ]),
    ).toMatchInlineSnapshot(
      `"{ error: true, type: 'Error', message: 'Message', cause: 'A cause' }"`,
    );
  });

  it('formats other common objects and primitives', () => {
    expect(
      formatToPrint([
        null,
        undefined,
        1,
        {
          level1: {
            level2: ['item 1', { item3Key: 'item3Value' }],
          },
        },
      ]),
    ).toMatchInlineSnapshot(`
      "null undefined 1 {
        level1: { level2: [ 'item 1', { item3Key: 'item3Value' } ] }
      }"
    `);
  });

  it('truncates the input message if it is too large', () => {
    const stringMessage = 'C'.repeat(2222);

    const formatted = formatToPrint([stringMessage]);

    expect(formatted).not.toEqual(stringMessage);

    expect(formatted).toMatchInlineSnapshot(
      `""`,
    );
  });
});
